mapper.select:
SELECT * FROM t_man_common_menus WHERE loginstyle=#{loginuc} AND modelstyle=#{modelstyle}
AND (id IN (SELECT menus_id FROM t_man_common_menus_level WHERE level=#{level} and (industryid= 0 or industryid=#{industryid})) OR id IN (SELECT DISTINCT(menus_id) FROM t_man_common_menus_role WHERE role_id IN (SELECT role_id FROM t_man_common_user_role WHERE bus_user_id=#{userId}))
) ORDER BY menus_pid,menus_order
